int get_percentage(void) {
    int percent = 0;
    int i, j;

    for (i = 0; i < MAP_WIDTH; i++) {
        for (j = 0; j < MAP_HEIGHT; j++) {
            if (i == 0 || j == 0 || i == MAP_WIDTH - 1 || j == MAP_HEIGHT - 1) {
                continue;
            }
            else if (tile_at(i, j) == TILE_FILLED) {
                ++percent;
            }
        }
    }

    percent = 100 * percent / (MAP_WIDTH * MAP_HEIGHT);

    return percent;
}

void print_percentage(void) {
    int i;
    int percent = get_percentage();

    for (i = 0; i < 3; i++) {
        int poslednja_cifra = percent % 10;

        if (poslednja_cifra == 0 && i > 0) {
        	continue;
            //undraw_sprite(15 - i, MAP_HEIGHT);
        }
        else {
            // numbers[poslednja_cifra] ce dati sliku za tu cifru
            draw_sprite(numbers[poslednja_cifra], 15 - i, MAP_HEIGHT);
        }

        // Skloni poslednju cifru
        percent = percent / 10;

    }

    draw_sprite(percent_sprite, 16, MAP_HEIGHT);
}


