void print_time(int minutes, int seconds) {
    int i;

    for (i = 0; i < 2; i++) {
        int poslednja_cifra = seconds % 10;
        draw_sprite(numbers1[poslednja_cifra], 10 - i, MAP_HEIGHT);
        seconds /= 10;
      // draw_sprite(dvotacka, 10 - 2, MAP_HEIGHT);
    }

    draw_sprite(dvotacka, 10- 2, MAP_HEIGHT);

    for (i = 0; i < 2; i++) {
        int poslednja_cifra = minutes % 10;
        draw_sprite(numbers1[poslednja_cifra], 10- 3 - i, MAP_HEIGHT);
        minutes /= 10;
    }
}

